// Adcionando o popover = forma de descrição que apareçe qnd um botão é clicado
$(function () {
    $('[data-toggle="popover"]').popover();
});
// tirando o efeito de carrosel da aba de downloads
$('#carouselSite2').carousel({
    interval: false
});
// faz com que os link ancora sejam feitos de forma bonita de forma bonitinha
// aparentemente há formas de fazer com css mas nenhuma funcionou
var $doc = $('html, body');
$('.classSuave').click(function () {
    $doc.animate({
        scrollTop: $($.attr(this, 'href')).offset().top
    }, 500);
    return false;
});
$(document).ready(function () {
    // Função personalizada que alterna entre classe stick e relativa
    // quando o menu desce essa classe faz com que quando o menu chegar no top do brownser ele fique fixo,
    // causndo assim a impressão de que o menu foi pego pelo brownser.
    var stickyToggle = function (sticky, stickyWrapper, scrollElement) {
        var stickyHeight = sticky.outerHeight();
        var stickyTop = stickyWrapper.offset().top;
        if (scrollElement.scrollTop() >= stickyTop) {
            stickyWrapper.height(stickyHeight);
            sticky.addClass("is-sticky");
        }
        else {
            sticky.removeClass("is-sticky");
            stickyWrapper.height('auto');
        }
    };
    // faz com que o seja adcionado o menu no top com a clase stick qnd o site é carregado ja que de outra forma ao ser atualizado a pessoa deveria subir e descer o site para o menu ficar fixo
    $('[data-toggle="sticky-onscroll"]').each(function () {
        var sticky = $(this);
        var stickyWrapper = $('<div>').addClass('sticky-wrapper'); // insert hidden element to maintain actual top offset on page
        sticky.before(stickyWrapper);
        sticky.addClass('sticky');

        // Scroll & resize events
        $(window).on('scroll.sticky-onscroll resize.sticky-onscroll', function () {
            stickyToggle(sticky, stickyWrapper, $(this));
        });
        // On page load
        stickyToggle(sticky, stickyWrapper, $(window));
    });
});